package com.vandersoncamp.rendimento.business.validate;import com.vandersoncamp.rendimento.business.EmprestimoBO;import com.vandersoncamp.rendimento.util.exception.BusinessException;import javax.enterprise.context.RequestScoped;import java.math.BigDecimal;/** * Classe de validação dos dados do empréstimo do cliente * @author vanderson.campanholi */@RequestScopedpublic class EmprestimoValidate {    public void validate(EmprestimoBO emprestimo) {        if (emprestimo.getValorSolicitado() == null) {            throw new BusinessException("Não foi informado o valor solicitado pelo cliente");        }        if (emprestimo.getMeses() == null) {            throw new BusinessException("Não foi informada a quantidade de meses para cálculo da simulação");        }        if (emprestimo.getCliente() == null) {            throw new BusinessException("Cliente não foi informado para cálculo do empréstimo");        }        if (!isNumerico(emprestimo.getMeses().toString())) {            throw new BusinessException("Para o campo meses são permitidos somente números inteiros");        }        if (emprestimo.getValorSolicitado().compareTo(BigDecimal.ZERO) <= 0) {            throw new BusinessException("O valor solicitado deve ser maior que zero");        }    }    private Boolean isNumerico(String numero) {        return numero.matches("\\d+");    }}