package com.vandersoncamp.rendimento.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.io.Serializable;@Entity@Table(name = "ENDERECO")@SequenceGenerator(name = "SEQ_ENDERECOS", sequenceName = "SEQ_ENDERECOS", initialValue = 1, allocationSize = 1)public class Endereco implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "SEQ_ENDERECOS")    private Long id;    @NotNull(message = "{Endereco.logradouro.NotNull}")    @Size(min = 1, max = 80)    @Column(name = "LOGRADOURO", nullable = false)    private String logradouro;    @NotNull    @Column(name = "NUMERO", nullable = false)    private Long numero;    @Size(max = 8)    @Column(name = "CEP", length = 8)    private String cep;    @Size(max = 50)    @Column(name = "BAIRRO", length = 50)    private String bairro;    @Size(max = 50)    @Column(name = "CIDADE", length = 50)    private String cidade;    @Size(max = 50)    @Column(name = "ESTADO", length = 50)    private String estado;    @Size(max = 255)    @Column(name = "COMPLEMENTO")    private String complemento;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getLogradouro() {        return logradouro;    }    public void setLogradouro(String logradouro) {        this.logradouro = logradouro;    }    public String getCep() {        return cep;    }    public void setCep(String cep) {        this.cep = cep;    }    public String getBairro() {        return bairro;    }    public void setBairro(String bairro) {        this.bairro = bairro;    }    public String getCidade() {        return cidade;    }    public void setCidade(String cidade) {        this.cidade = cidade;    }    public String getEstado() {        return estado;    }    public void setEstado(String estado) {        this.estado = estado;    }    public Long getNumero() {        return numero;    }    public void setNumero(Long numero) {        this.numero = numero;    }    public String getComplemento() {        return complemento;    }    public void setComplemento(String complemento) {        this.complemento = complemento;    }}