package com.vandersoncamp.rendimento.business;import com.vandersoncamp.rendimento.model.Cliente;import com.vandersoncamp.rendimento.model.RiscoEnum;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import java.math.BigDecimal;import static org.junit.jupiter.api.Assertions.*;public class EmprestimoBOTest {    private Cliente getCliente() {        Cliente cliente = new Cliente();        cliente.setId(1L);        cliente.setNome("Teste 001");        cliente.setRendimentoMensal(BigDecimal.TEN);        cliente.setRisco(RiscoEnum.A);        return cliente;    }    private EmprestimoBO getEmprestimo() {        EmprestimoBO emprestimo = new EmprestimoBO();        emprestimo.setCliente(getCliente());        emprestimo.setValorSolicitado(BigDecimal.TEN);        emprestimo.setMeses(2L);        emprestimo.setValorSimulacao(BigDecimal.TEN);        return emprestimo;    }    @Test    @DisplayName("Test 01")    public void test01() {        assertNotNull(getEmprestimo().getCliente());    }    @Test    @DisplayName("Test 02")    public void test02() {        assertEquals(getEmprestimo().getValorSolicitado(), BigDecimal.TEN);    }    @Test    @DisplayName("Test 03")    public void test03() {        assertEquals(getEmprestimo().getMeses().longValue(), 2L);    }    @Test    @DisplayName("Test 04")    public void test04() {        assertEquals(getEmprestimo().getValorSimulacao(), BigDecimal.TEN);    }}